mapper.select:
SELECT t.f_id,t.f_title,t.f_price,t.f_consult_price,t.f_new_price,t.f_show_price, t.f_showflag,t.f_end_time,t.f_address,t.f_transfer_num,t.f_transfer_price_state, t.f_mileage,t.f_upbk_time,t.f_dw,t.f_pl,t.f_bk_citiy,t.f_xqbz,t.f_remark,t.f_status,t.f_top_img, t.f_imgs,t.f_transaction_statu,t.f_offer_statue,t.f_brand,t.f_type,t.f_pk_id,t.f_str_a,t.f_str_b, t.f_int_a,t.f_int_b,t.`f_createtime`,t.`f_first_price`,b.`f_file_path` AS c_top_img_str FROM `t_car_info` t LEFT JOIN t_base_uploadfilerecode b ON t.`f_top_img` = b.`f_id`
ORDER BY f_id DESC
ORDER BY ${orderField}
