SELECT t.f_id,t.f_data_id,t.f_mater_code,t.f_mater_name,t.f_versioncode,t.f_cigbrand, t.f_abroadflag,t.f_casebarcode,t.f_barbarcode,t.f_boxbarcode,t.f_barstatus,t.f_ismajorbar, t.f_productent,t.f_productpoint,t.f_trademarkowner,t.f_subcomp,t.f_allotprice0,t.f_allotprice1, t.f_bulkprice,t.f_saleprice,t.f_pricetype,t.f_cigtype,t.f_filtertype,t.f_producttype, t.f_cigmaincolor,t.f_cigasscolor,t.f_cigenname,t.f_cigbrandenname,t.f_packageform,t.f_packagestandards, t.f_cigtotallen,t.f_ciglength,t.f_filterlength,t.f_ciggirth,t.f_cigtarcontent,t.f_cignicotinic, t.f_cigco,t.f_notsaleflag,t.f_cigislow,t.f_cigisgreat,t.f_cigistrial,t.f_jointunit, t.f_innerbalance,t.f_salescope,t.f_isusing,t.f_cigapprvdate,t.f_cigltstupdate,t.f_cigtax, t.f_remcode,t.f_cigmemo,t.f_inventore_id,t.f_cigarettetype,t.f_standardscode,t.f_formulacode, t.f_packingcode,t.f_allotprice2,t.f_allotprice3,t.f_allotprice4,t.f_allotprice5,t.f_link_tobacco_cut, t.f_status,t.f_created_date,t.f_created_person,t.f_updated_date,t.f_updated_person, b1.`f_bar_name` AS barName, c1.f_bra_name AS braName, c3.`f_len_name` AS lenName, c5.`f_prod_name` AS prodName, c6.`f_cig_name` AS cigName, c7.`f_ver_name` AS verName, f.`f_filter_name` AS filterName, p1.`f_pac_name` AS pacName, p2.`f_paci_name` AS paciName, p3.`f_pacs_name` AS pacsName, p5.`f_pri_name` AS priName, p6.`f_pro_name` AS proName, t1.`f_tar_name` AS tarName, t3.`f_tran_name` AS tranName FROM `t_base_materialinfo` AS t LEFT JOIN `t_mater_barstatus` AS b1 ON t.f_barstatus = b1.`f_data_id` LEFT JOIN `t_mater_cigbrand` AS c1 ON t.f_cigbrand = c1.`f_data_id` LEFT JOIN `t_mater_ciglength` AS c3 ON t.f_ciglength = c3.`f_data_id` LEFT JOIN `t_mater_cigprodtype` AS c5 ON t.f_producttype = c5.`f_data_id` LEFT JOIN `t_mater_cigtype` AS c6 ON t.f_cigtype = c6.`f_data_id` LEFT JOIN `t_mater_cigversion` AS c7 ON t.f_versioncode = c7.`f_data_id` LEFT JOIN `t_mater_filtertip` AS f ON t.f_filtertype = f.`f_data_id` LEFT JOIN `t_mater_packageform` AS p1 ON t.f_packageform = p1.`f_data_id` LEFT JOIN `t_mater_packidentifier` AS p2 ON t.f_packingcode = p2.`f_data_id` LEFT JOIN `t_mater_packspec` AS p3 ON t.f_packagestandards = p3.`f_data_id` LEFT JOIN `t_mater_pricetype` AS p5 ON t.f_pricetype = p5.`f_data_id` LEFT JOIN `t_mater_productpoint` AS p6 ON t.f_productpoint = p6.`f_data_id` LEFT JOIN `t_mater_tarvalegrade` AS t1 ON t.f_cigtarcontent = t1.`f_data_id` LEFT JOIN `t_mater_transnation` AS t3 ON t.f_salescope = t3.`f_data_id`
ORDER BY f_id DESC
ORDER BY ${orderField}
