mapper.select:
SELECT t.f_id, f_mainorder_id, f_mater_code, f_mater_name, f_quality, f_batch_no, f_mater_version, f_mater_cigbrand, f_actual_quality, f_entry_complite_date as f_produce_date, f_cigtype FROM t_ware_entrymainorder AS we left JOIN t_ware_entryorderdetail AS t on we.f_id = t.f_mainorder_id
1=1 and we.f_status!=0
AND t.f_batch_no like #{whereCondition.batchNoByLike,jdbcType=VARCHAR}
AND ( t.f_batch_no like #{keyWordByLike,jdbcType=VARCHAR} )
ORDER BY t.f_id DESC
ORDER BY ${orderField}

SELECT COUNT(1)
FROM
t_ware_entrymainorder AS we left JOIN
t_ware_entryorderdetail AS t on we.f_id = t.f_mainorder_id and we.f_status!=0

	1=1
	and we.f_status!=0

		AND t.f_batch_no like #{whereCondition.batchNoByLike,jdbcType=VARCHAR}


		AND (
				t.f_batch_no like #{keyWordByLike,jdbcType=VARCHAR}   
			)




		ORDER BY t.f_id DESC


		ORDER BY ${orderField}

SELECT t1.*,
(case when t1.f_cigtype=0 THEN (select f_conver_coef from t_base_materialinfo tt1 LEFT JOIN t_mater_packspec tt2 on tt1.f_packagestandards=tt2.f_data_id where tt1.f_mater_code=t1.f_mater_code) ELSE (select f_conver_coef from t_base_testcigarette tt3 LEFT JOIN t_mater_packspec tt4 on tt3.f_packagestandards=tt4.f_data_id where tt3.f_code=t1.f_mater_code) END) as converCoef
FROM t_ware_entryorderdetail t1 WHERE f_mainorder_id = #{mainorderId}
