mapper.select:
SELECT a.f_batch_no,a.f_quality,a.f_cigtype,c.f_mater_code,c.f_mater_name, c.f_mater_version,c.f_mater_cigbrand,a.f_sole_code,a.f_tray_code, a.f_tray_rfid,a.f_local_code,e.f_floor_area_code, a.f_subver_id,a.f_subver_name,f_descript FROM t_ware_inventorytraylocalmap a LEFT JOIN t_ware_inventorydetail b ON a.f_detail_id = b.f_id LEFT JOIN t_ware_inventorymain c ON b.f_main_id = c.f_id LEFT JOIN t_base_locationinfo d ON a.f_local_id = d.f_id LEFT JOIN t_base_areainfo e ON d.f_area_id = e.f_id WHERE 1=1
AND e.f_floor_area_code = #{whereCondition.floorAreaCode,jdbcType=VARCHAR}
AND a.f_local_code LIKE CONCAT(#{whereCondition.localCode,jdbcType=VARCHAR},'-%')
AND c.f_mater_name LIKE CONCAT('%',#{whereCondition.materName,jdbcType=VARCHAR},'%')
AND a.f_cigtype = #{whereCondition.cigtype,jdbcType=INTEGER}
ORDER BY a.f_local_code

SELECT pd.f_id as posDetailId, pd.f_main_id, pd.f_orig_tray_rfid, (SELECT t2.f_descript FROM t_ware_positionadjustdetail as t1 LEFT JOIN t_base_locationinfo as t2 on t1.f_orig_local_code=t2.f_local_code WHERE t1.f_orig_local_code=pd.f_orig_local_code LIMIT 1) as oldDescript, (SELECT t2.f_descript FROM t_ware_positionadjustdetail as t1 LEFT JOIN t_base_locationinfo as t2 on t1.f_dest_local_code=t2.f_local_code WHERE t1.f_dest_local_code=pd.f_dest_local_code LIMIT 1) as newDescript, pd.f_orig_local_code, pd.f_dest_local_code, pd.f_operator, pd.f_operator_id, pd.f_mater_code, pd.f_mater_name, pd.f_mater_version, pd.f_mater_cigbrand, pd.f_sole_code, pd.f_status as posDetailStatus, pd.f_tray_code, pd.f_inventorytraylocalmap_id, pd.f_cigtype, p.f_id, p.f_order_number, p.f_status, p.f_remark, p.f_complit_date, p.f_created_date, p.f_created_person_id, p.f_created_person, p.f_updated_date, p.f_updated_person_id, p.f_updated_person, p.f_floor_code, p.f_floor_name FROM t_ware_positionadjustmain AS p LEFT JOIN t_ware_positionadjustdetail AS pd ON p.f_id = pd.f_main_id
1=1 and pd.f_status = 0
AND p.f_floor_code = #{whereCondition.floorCode,jdbcType=VARCHAR} AND p.f_floor_name like #{whereCondition.floorName,jdbcType=VARCHAR}
ORDER BY p.f_id DESC
ORDER BY ${orderField}

SELECT COUNT(1) FROM t_ware_positionadjustmain AS p LEFT JOIN t_ware_positionadjustdetail AS pd ON p.f_id = pd.f_main_id
1=1 and pd.f_status = 0
AND p.f_floor_code = #{whereCondition.floorCode,jdbcType=VARCHAR} AND p.f_floor_name like #{whereCondition.floorName,jdbcType=VARCHAR}
